mapper.select:
SELECT J.PRICE, J.ACCOUNT_CODE, J.DEBIT_CREDIT
FROM SLIP S
RIGHT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year}
AND TO_NUMBER(TO_CHAR(S.SLIP_DATE, 'MM')) <= #{month}
AND COM_CODE = #{comCode}

SELECT J.PRICE, J.ACCOUNT_CODE, J.DEBIT_CREDIT
FROM SLIP S
RIGHT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year}
AND TO_CHAR(S.SLIP_DATE, 'MMDD') <= LPAD(#{month}, 2, 0) || LPAD(#{date}, 2, 0)
AND COM_CODE = #{comCode}

SELECT S.SLIP_DATE, S.DATE_SLIP_CODE, S.BRIEF, J.VENDER_CODE, V.VENDER_NAME, J.DEBIT_CREDIT, J.PRICE, A.ACCOUNT_TITLE 
FROM SLIP S
RIGHT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
LEFT JOIN VENDER V ON(J.VENDER_CODE = V.VENDER_CODE)
LEFT JOIN ACCOUNT_TITLE A ON(J.ACCOUNT_CODE = A.ACCOUNT_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year}
AND TO_NUMBER(TO_CHAR(S.SLIP_DATE, 'MM')) <= #{month}
AND S.COM_CODE = #{comCode}
AND J.ACCOUNT_CODE = #{accountCode}
ORDER BY S.SLIP_DATE

SELECT S.SLIP_DATE, S.DATE_SLIP_CODE, S.BRIEF, J.VENDER_CODE, V.VENDER_NAME, J.DEBIT_CREDIT, J.PRICE, A.ACCOUNT_TITLE FROM SLIP S RIGHT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE) LEFT JOIN VENDER V ON(J.VENDER_CODE = V.VENDER_CODE) LEFT JOIN ACCOUNT_TITLE A ON(J.ACCOUNT_CODE = A.ACCOUNT_CODE) WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year} AND TO_CHAR(S.SLIP_DATE, 'MMDD') <= LPAD(#{month}, 2, 0) || LPAD(#{date}, 2, 0) AND S.COM_CODE = #{comCode} AND J.ACCOUNT_CODE IN
#{item}
ORDER BY S.SLIP_DATE

SELECT COUNT(*)
FROM MFRG_STMT
WHERE COM_CODE = #{comCode}
AND YEAR = #{year}

SELECT COUNT(*)
FROM MFRG_STMT
WHERE COM_CODE = #{comCode}
AND YEAR = #{year}
AND CLOSING = 'N'

SELECT COUNT(*)
FROM MFRG_STMT
WHERE COM_CODE = #{comCode}
AND YEAR = #{year}
AND CLOSING = 'Y'

mapper.update:
UPDATE MFRG_STMT
SET VAL13 = #{val13}, SUM90 = #{sum90}, CLOSING = #{closing}
WHERE COM_CODE = #{comCode}
AND YEAR = #{year}
AND MFRG_STMT_CODE = (SELECT MAX(MFRG_STMT_CODE)
                      FROM MFRG_STMT
                      WHERE YEAR = #{year})

UPDATE INCOME_STMT
SET CLOSING = #{closing}, SUM10 = #{sum10}, V40100 = #{v40100}, VAL213 = #{val213}, SUM30 = #{sum30}, SUM40 = #{sum40}, SUM50 = #{sum50}, SUM100 = #{sum100}
WHERE COM_CODE = #{comCode}
AND YEAR = #{year}
AND INCOME_STMT_CODE = (SELECT MAX(INCOME_STMT_CODE)
				   		FROM INCOME_STMT
				   		WHERE YEAR = #{year})

