mapper.select:
SELECT J.PRICE, J.ACCOUNT_CODE
FROM SLIP S
LEFT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year}
AND TO_NUMBER(TO_CHAR(S.SLIP_DATE, 'MM')) <= #{month}
AND COM_CODE = #{comCode}

SELECT J.PRICE, J.ACCOUNT_CODE
FROM SLIP S
LEFT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year} - 1
AND TO_NUMBER(TO_CHAR(S.SLIP_DATE, 'MM')) <= #{month}
AND COM_CODE = #{comCode}

SELECT S.SLIP_DATE, S.DATE_SLIP_CODE, S.BRIEF, J.VENDER_CODE, V.VENDER_NAME, J.DEBIT_CREDIT, J.PRICE 
FROM SLIP S
LEFT JOIN JOURNALIZE J ON(S.SLIP_CODE = J.SLIP_CODE)
LEFT JOIN VENDER V ON(J.VENDER_CODE = V.VENDER_CODE)
WHERE TO_CHAR(S.SLIP_DATE, 'YYYY') = #{year}
AND TO_NUMBER(TO_CHAR(S.SLIP_DATE, 'MM')) <= #{month}
AND S.COM_CODE = #{comCode}
AND J.ACCOUNT_CODE = #{accountCode}
ORDER BY S.SLIP_DATE
